mapper.insert:
        /*insertTodo*/
        INSERT INTO
			tododetail
            (
                 categoryId
                , todoTitle
                , todoContent
                , todoDay
                , todoTime
                , todoState
                , todoCreatedAt
            )

		VALUES
			(
                UNHEX(#{categoryId})
		      , #{todoTitle}
              , #{todoContent}
              , #{todoDay}
              , #{todoTime}
              , 'TR'
              , now()
        )

mapper.update:
/*updateTodo*/
UPDATE
    tododetail
SET
    categoryId=UNHEX(#{categoryId})
  , todoTitle=#{todoTitle}
  , todoContent=#{todoContent}
  , todoDay=  DATE_FORMAT(#{todoDay}, '%Y-%m-%d')
  , todoTime=DATE_FORMAT(#{todoTime}, '%H:%i:%s')
  , todoState=#{todoState}
  , todoUpdatedAt=now()
WHERE
    todoNo=#{todoNo}
