mapper.update:
/*updateTodo*/
UPDATE
    testtododetail
SET
    categoryId=UNHEX(#{categoryId})
  , todoTitle=#{todoTitle}
  , todoContent=#{todoContent}
  , todoDay=  DATE_FORMAT(#{todoDay}, '%Y-%m-%d')
  , todoTime=DATE_FORMAT(#{todoTime}, '%H:%i:%s')
  , todoState=#{todoState}
  , todoUpdatedAt=now()
WHERE
    HEX(categoryId) = #{categoryId}
AND
    todoNo=#{todoNo}

mapper.delete:
/*deleteTodo*/
DELETE
FROM
    testtododetail
WHERE
    HEX(categoryId) = #{categoryId}
  AND
    todoNo=#{todoNo}
