mapper.insert:
        /*insertTodo*/
        INSERT INTO
			tododetail
            (
                todoTitle
                , todoContent
                , todoDay
                , todoTime
                , todoState
                , todoCreatedAt
            )

		VALUES
			(
		        #{todoTitle}
              , #{todoContent}
              , #{todoDay}
              , #{todoTime}
              , 'TR'
              , now()
        )
        ON DUPLICATE KEY UPDATE todoNo = LAST_INSERT_ID() + 1

mapper.update:
/*updateTodo*/
UPDATE
    tododetail
SET
    todoTitle=#{todoTitle}
  , todoContent=#{todoContent}
  , todoDay=  DATE_FORMAT(#{todoDay}, '%Y-%m-%d')
  , todoTime=DATE_FORMAT(#{todoTime}, '%H:%i:%s')
  , todoUpdatedAt=now()
WHERE
    todoNo=#{todoNo}
